import React from "react";

import { useState, useMemo } from "react";

import styled from "styled-components";

import bg from "./img/bg.png";

import { MainLayout } from "./styles/Layouts";

import Orb from "./Components/Orb/Orb";

import Navigation from "./Components/Navigation/Navigation";

import Dashboard from "./Components/Dashboard/Dashboard"; // Assuming this is your custom Dashboard component

import Income from "./Components/Income/Income";

import Expenses from "./Components/Expenses/Expenses";

import { useGlobalContext } from "./context/globalContext";

import {
  SignedIn,
  SignedOut,
  SignInButton,
  UserButton,
} from "@clerk/clerk-react";

import { ClerkProvider, useClerk } from "@clerk/clerk-react";

// Replace with your actual Clerk frontend API key

const CLERK_FRONTEND_API_KEY = "YOUR_FRONTEND_API_KEY";

function App() {
  const [active, setActive] = useState(1);

  const global = useGlobalContext();

  const displayData = () => {
    switch (active) {
      case 1:
        return <Dashboard />; // Assuming Dashboard renders dashboard content

      case 2:
        return <Dashboard />; // Placeholder for potentially different dashboard view

      case 3:
        return <Income />;

      case 4:
        return <Expenses />;

      default:
        return <Dashboard />;
    }
  };

  const orbMemo = useMemo(() => {
    return <Orb />;
  }, []);

  return (
    <ClerkProvider>
         
      <AppStyled bg={bg} className="App">
            {orbMemo}
            
        <MainLayout>
               
          <Navigation active={active} setActive={setActive} />
               
          <main>
                  
            <SignedIn>
                     {/* Access user data if needed (optional) */}
                     
              {useClerk().user && (
                <p>
                           Welcome back, {useClerk().user.firstName}{" "}
                  {useClerk().user.lastName}!         
                </p>
              )}
                     {displayData()}
                    
            </SignedIn>
                  
            <SignedOut>
                     <p>Please sign in to access the dashboard.</p>
                     
              <SignInButton />
                    
            </SignedOut>
                 
          </main>
              
        </MainLayout>
           
      </AppStyled>
        
    </ClerkProvider>
  );
}

const AppStyled = styled.div`

 height: 100vh;

 background-image: url(${(props) => props.bg});

 position: relative;

 main {

  flex: 1;

  background: rgba(252, 246, 249, 0.78);

  border: 3px solid #FFFFFF;

  backdrop-filter: blur(4.5px);

  border-radius: 32px;

  overflow-x: hidden;

  &::-webkit-scrollbar {

   width: 0;

  }

 }

`;

export default App;
